head.script:
scwin.onpageload = function () {
};

/**
 * 게시물 단건조회
 */
scwin.fn_searchBoardInfo = function () {
	dma_search.set('QUERY',"M.selectBoardInfo");
	com.sbm.execute(sbm_boardInfo);
	//callback
	scwin.sbm_searchBoardInfo_submitdone = function(e) {
		edi_1.setHTML(dma_map.get('BOARD_CONT'));
		scwin.fn_searchFile();
	};
};

/* 게시물 단건 첨부파일 목록조회 */
scwin.fn_searchFile = function () {
	dma_search.set('QUERY',"M.selectBoardFileList");
	com.sbm.execute(sbm_boardFileList);
	//callback
	scwin.sbm_boardFileList_submitdone = function(e) {
		txt_cnt.setValue(dlt_boardFile.getRowCount()+"건");
	};
};

scwin.fn_bytesToLabel = function(_byte){
	var byteSize = ["Byte","KB", "MB", "GB"];
	  var _kb = _byte/1024;
	  var reValue;
	  if(_kb < 1){
	      reValue = _kb.toFixed(1) + byteSize[0]
	  }else if(1 < _kb  && _kb < 1024){
	      reValue = _kb.toFixed(1) + byteSize[1];//kb
	  }else if(1024 < _kb && _kb < 1048576){
	      reValue = (_kb /1024).toFixed(1) + byteSize[2];//mb
	  }else if( _kb >= 1048576){
	      reValue = ((_kb /1024)/1024).toFixed(1) + byteSize[3];//gb
	  }
	  return reValue;
};

/* 게시물등록 */
scwin.btn_confirm_onclick = function(e) {
	com.win.confirm($p, '해당 게시물을 등록하시겠습니까?', function(e){
		if(e.clickValue){
			dma_search.set("BOARD_CONT", edi_1.getHTML());
			dma_search.set("BOARD_TITLE", dma_search.get("BOARD_CONT").substr(0,10));
			com.sbm.execute(sbm_insertBoardInfo);
		}
	});
	scwin.sbm_insertBoardInfo_submitdone = function(e) {
		if(e.responseJSON.status == "S"){
			var param = {
				"message":"등록이 완료되었습니다."
			};
			com.win.closePopup(param);
		}

	};
};

/* 팝업취소 */
scwin.btn_cancel_onclick = function(e) {
	com.win.closePopup();
};

/* 게시물 논리삭제 */
scwin.btn_del_onclick = function(e) {
    com.win.confirm($p, '해당 게시물을 삭제하시겠습니까?', function(e){
		if(e.clickValue){
			dma_search.set("STATUS", "U");
			dma_search.set("BOARD_DELYN","Y");
			com.sbm.execute(sbm_updateBoardInfo);
		}
	});
};

/* 게시물 물리삭제 */
scwin.btn_adminDel_onclick = function(e) {
	var data = {callbackFn: "scwin.delPop_callback" };
	var options = {
		id: "delPop",
		popupName: "게시물삭제",
		modal: true,
		useMaximize : true,
		width: 420, height: 116
	};
	com.win.openPopup("/ui/delPop.xml", options, data);
};
scwin.delPop_callback = function(e){
	if(e.delYn){
		com.win.confirm($p, '삭제하시면 데이터를 더이상 복구하실 수 없습니다.\n 해당 게시물을 삭제하시겠습니까?', function(e){
			if(e.clickValue){
				dma_search.set("STATUS", "D");
				com.sbm.execute(sbm_updateBoardInfo);
			}
		})
	}
}
/* 게시물 수정 */
scwin.btn_update_onclick = function(e) {
	com.win.confirm($p, '해당 게시물을 수정하시겠습니까?', function(e){
		if(e.clickValue){
			dma_search.set("STATUS", "U");
			dma_search.set("BOARD_CONT", edi_1.getHTML());
			dma_search.set("BOARD_TITLE", dma_search.get("BOARD_CONT").substr(0,10));
			com.sbm.execute(sbm_updateBoardInfo);
		}
	});
};

/* 게시물 수정,삭제 callback */
scwin.sbm_updateBoardInfo_submitdone = function(e) {
	if(e.responseJSON.status == "S"){
		if(dma_search.get('STATUS') == "D" || dma_search.get('BOARD_DELYN') == "Y"){
			var param = {
				"message":"삭제가 완료되었습니다."
			};
			com.win.closePopup(param);
		}else{
			var param = {
				"message":"수정이 완료되었습니다."
			};
			com.win.closePopup(param);
		}
	}
};

scwin.grd_program_oncelldblclick = async function(row,col,colId) {
	let _id = dlt_boardFile.getCellData(row,"BOARDFILE_NAMESERVER");
	googleDownload2(_id);
};

scwin.edi_1_onload = function() {
	scwin.param = com.data.getParameter();
	dma_search.setJSON(scwin.param.data.rowJson);
	dma_search.set("BOARD_TYPE",scwin.param.data.boardInfo.boardType)
	if(scwin.param.data.boardInfo.erpYn == "Y"){
		dma_search.set("BOARD_TABLE","MT_BOARD_ERP");
		dma_search.set("BOARD_FILE_TABLE","MT_BOARDFILE_ERP")
	}else{
		dma_search.set("BOARD_TABLE","MT_BOARD");
		dma_search.set("BOARD_FILE_TABLE","MT_BOARDFILE")
	}
	if(scwin.param.data.mode == "read"){
		btn_update.show('');
		btn_del.show('');
		btn_adminDel.show('');
		btn_confirm.hide();
		scwin.fn_searchBoardInfo();
	}else{
		tr_read.hide();
		ibx_boardWriter.setValue('superadmin');
	}
};

scwin.thumbnail = (_id)=>{
	return "<img src='https://drive.google.com/thumbnail?sz=s24&id="+_id+ "'/>";
}

scwin.btn_fileUpload_onclick = function(e) {
	var data = {callbackFn: "scwin.filePop_callback" };
	var options = {
		id: "filePop",
		popupName: "파일업로드",
		modal: true,
		useMaximize : false,
		width: 300, height: 282
	};
	com.win.openPopup("/ui/filePop.xml", options, data);
};

scwin.filePop_callback = function(resultArr){
	//dlt_boardFile"
	/*
		fileLength: "15"
		fileSize: "15"
		id: "1BgqoKb4X5IrCChYDoR7AbXJiSfu6SNDR"
		originalFilename: "20230405_173032 (8).jpg"
		physicalFilename: "1BgqoKb4X5IrCChYDoR7AbXJiSfu6SNDR"
		physicalFilepath: "17zolc486M-P5IdrioullpMSxdybixHYc"
		subDir: "board"
		uploadDir: "drive#file"
	*/
	/*
		<w2:column id="BOARDFILE_IDX" 			name="파일아이디" 			dataType="text"></w2:column>
		<w2:column id="BOARD_IDX" 				name="컨텐츠아이디" 		dataType="text"></w2:column>
		<w2:column id="BOARDFILE_NAME" 			name="원본파일명" 			dataType="text"></w2:column>
		<w2:column id="BOARDFILE_NAMESERVER" 	name="구글파일아이디" 		dataType="text"></w2:column>
		<w2:column id="BOARDFILE_PATH" 			name="구글폴더아이디" 		dataType="text"></w2:column>
		<w2:column id="BOARDFILE_SIZE" 			name="파일용량" 			dataType="text"></w2:column>
		<w2:column id="BOARDFILE_ID" 			name="구글파일아이디" 		dataType="text"></w2:column>
		<w2:column id="THUMBNAIL" 				name="가상썸네일이미지태그" 	dataType="text"></w2:column>
	*/
	if(resultArr){
		let arr = [];
		for(let i=0; i < resultArr.length; i++){
			let aFile = resultArr[i];
			let reFile = {};
			reFile.BOARDFILE_NAME = aFile.originalFilename;
			reFile.BOARDFILE_NAMESERVER = aFile.physicalFilename;
			reFile.BOARDFILE_ID = aFile.physicalFilename;
			reFile.BOARDFILE_PATH = aFile.physicalFilepath;
			reFile.BOARDFILE_SIZE = aFile.fileSize;
			arr.push(reFile);
		}
		dlt_boardFile.insertJSON(dlt_boardFile.getRowCount(), arr);
	}
};
