	swiperScript.setAttribute('src',swiperURL);
	swiperScript.onload = function(e){


		/* 스케줄러 클릭 */
		scwin.sch_cal1_onclick = function(retObj,b,c) {

			//debugger;
			//alert('click isMobile:'+com.util.isMobile());
/*

