			alert('이벤트타깃 class:'+event.target.getAttribute('class'));
			alert('이벤트타깃 tagName:'+event.target.tagName);
			alert('이벤트타깃 innerText:'+event.target.innerText);

					alert('커런트타깃1:'+event.currentTarget.nodeType);
					alert('커런트타깃2:'+event.currentTarget.DOCUMENT_TYPE_NODE);
					alert(c +'커런트타깃3:'+event.currentTarget.contentType);
			*/

			//mf_wfm_header_txt_empNm.setValue('sch_cal1_onclick');
			//mf_wdc_main_subWindow0_wframe_pgt_tit.setValue('onclick');
			if(!com.util.isMobile() || c || !event.currentTarget.contentType){
				//!com.util.isMobile() 는 pc에서 실행하는경우
				//c 는 swiper가 실행하는경우 (모바일만해당됨)
				//event.currentTarget.contentType is null이면 '+'눌러서나온 레이어에서 클릭한것임 => 이경우는 swiper타지않음 (모바일만해당됨)
				if(retObj.title){
					retObj.id = dlt_list.getMatchedJSON('SCH_TITLE',retObj.title,false)[0].groupId;
				}
				let _param = {};
				let _popupName = '';
				let _height = '';
				retObj.start.setHours(retObj.start.getHours() - 9);
				retObj.end.setHours(retObj.end.getHours() - 9);
				retObj.end.setSeconds(retObj.end.getSeconds() -1)
				_param.schInfo = {
					"START":scwin.dateToString(retObj.start).full,
					"END":scwin.dateToString(retObj.end).full
				};
				if(retObj.id){
					_param.schInfo.SCH_IDX = retObj.id;
					_popupName= "일정상세";
					_param.mode = "read";
				}else{
					_popupName = "일정등록";
					_param.mode = "write";
					_param.writer = JSON.parse(sessionStorage.getItem("loginInfo")).name;
				}
				var data = { data: _param, callbackFn: "scwin.schedulePop_callback" };
				var options = {
					id: "schedulePop",
					popupName: _popupName,
					modal: true,
					useMaximize : true,
					width: 500, height: 402,
					className : "h100"
				};

				com.win.openPopup("/ui/schedulePop.xml", options, data);

			}

		};

		var swiper = new Swiper(".mySwiper", {
			rewind: true
		});
		swiper.on('slideChange', function (a,b,c) {
			let month = $p.getComponentById(a.slides[a.activeIndex].id).getUserData('month');
			SCH_CURRENTMONTH.setValue(month);
		});
		swiper.onClickChild = scwin.sch_cal1_onclick;
		scwin.swiper = swiper;

		scwin.fn_search();
	}
	document.body.insertBefore(swiperScript,document.body.firstChild);
	$(".btn_fav").click(function(){
		$(".btn_fav").toggleClass("on");
	});

};

/* 일정목록조회 */
scwin.fn_search = function(){
	let _yyyymm = sch_cal.getView().title.replace(/년|월|\s/g,'');

	if(_yyyymm == ''){
		_yyyymm = WebSquare.date.getCurrentServerDate('YYYYMM');
	}
	if(_yyyymm.length == 5){
		_yyyymm = _yyyymm.substr(0,4)+'0'+_yyyymm.substr(4,1);
	}
	dma_search.set("SCH_CURRENTMONTH",_yyyymm);
	SCH_CURRENTMONTH.setValue(_yyyymm);


	let _yyyymm_next = WebSquare.date.dateTimeAdd( _yyyymm,  1, "month" );
	let _yyyymm_prev = WebSquare.date.dateTimeAdd( _yyyymm, -1, "month" );
	dma_search.set("SCH_CURRENTMONTH_PREV",_yyyymm_prev);
	dma_search.set("SCH_CURRENTMONTH_NEXT",_yyyymm_next);

	let _yyyymm_nnext = WebSquare.date.dateTimeAdd( _yyyymm,  2, "month" );
	let _yyyymm_pprev = WebSquare.date.dateTimeAdd( _yyyymm, -2, "month" );
	dma_search.set("SCH_CURRENTMONTH_PPREV",_yyyymm_pprev);
	dma_search.set("SCH_CURRENTMONTH_NNEXT",_yyyymm_nnext);




	com.sbm.execute(sbm_schdList);

	scwin.sbm_schdList_submitdone = function(e) {
		let _yyyymm = SCH_CURRENTMONTH.getValue();	
		scwin.setMonth(sch_cal,_yyyymm);

		let _yyyymm_next = WebSquare.date.dateTimeAdd( _yyyymm,  1, "month" );
		let _yyyymm_prev = WebSquare.date.dateTimeAdd( _yyyymm, -1, "month" );
		scwin.setMonth(sch_cal_next,_yyyymm_next);
		scwin.setMonth(sch_cal_prev,_yyyymm_prev);

		let _yyyymm_nnext = WebSquare.date.dateTimeAdd( _yyyymm,  2, "month" );
		let _yyyymm_pprev = WebSquare.date.dateTimeAdd( _yyyymm, -2, "month" );
		scwin.setMonth(sch_cal_nnext,_yyyymm_nnext);
		scwin.setMonth(sch_cal_pprev,_yyyymm_pprev);


		scwin.swiper.slideTo(2,1);
	};
};
scwin.setMonth = (_schObj,_month)=>{
	_schObj.setUserData('month',_month);
	$("#" + _schObj.id).fullCalendar("gotoDate", _month+'01');
};

/* 스케줄러 상단버튼 클릭 */
scwin.scheduleCalendar1_onheaderbtnclick = function(buttonType,) {
	scwin.fn_search();
};

/* SCH_COLOR importFormatter */
scwin.fn_format = function(rowJson, row, col) {
	let _valJson = {"color":rowJson.SCH_COLOR};
	return _valJson;
};


/* 팝업 callback */
scwin.schedulePop_callback = function(e) {
	com.win.alert(e.message);
	scwin.fn_search();
}

//util
scwin.dateToString = function(date){
	['yyyyMMddhhmmss, 월:MM,일:dd 시분:hh:mm'];
    var year = date.getFullYear().toString();
    var month = date.getMonth() + 1;
    month = month < 10 ? '0' + month.toString() : month.toString();
    var day = date.getDate();
    day = day < 10 ? '0' + day.toString() : day.toString();
    var hour = date.getHours();
    hour = hour < 10 ? '0' + hour.toString() : hour.toString();
    var minites = date.getMinutes();
    minites = minites < 10 ? '0' + minites.toString() : minites.toString();
    var seconds = date.getSeconds();
    seconds = seconds < 10 ? '0' + seconds.toString() : seconds.toString();
	var returnObj = {};
	returnObj["year"] = year;
	returnObj["month"] = month;
	returnObj["day"] = day;
	returnObj["hour"] = hour;
	returnObj["minites"] = minites;
	returnObj["seconds"] = seconds;
	returnObj["seconds"] = seconds;
	returnObj["full"] = year + month + day+ hour+ minites+ seconds;
	returnObj["fullFormat"] = year +'-'+ month +'-'+ day+' ' + hour+':' + minites+':' + seconds;
	returnObj["년"] = year;
	returnObj["월"] = month;
	returnObj["일"] = day;
	returnObj["시"] = hour;
	returnObj["분"] = minites;
	returnObj["초"] = seconds;
	returnObj["시:분"] = hour+":"+minites;
    return returnObj;
}

scwin.btn_prev_onclick = function(e) {
	SCH_CURRENTMONTH.setValue(WebSquare.date.dateTimeAdd( SCH_CURRENTMONTH.getValue(), -1, "month" ));
};

scwin.btn_next_onclick = function(e) {
	SCH_CURRENTMONTH.setValue(WebSquare.date.dateTimeAdd( SCH_CURRENTMONTH.getValue(), +1, "month" ));
};

