head.script:
scwin.onpageload = function () {
	scwin.param = com.data.getParameter();
	dma_schdInfo.set("SCH_START",scwin.param.data.schInfo.START);
	dma_schdInfo.set("SCH_END",scwin.param.data.schInfo.END);
	if(scwin.param.data.mode == "read"){
		dfbox.show('');
		btn_update.show('');
		btn_del.show('');
		btn_success.show('');
		btn_adminDel.show('');
		btn_confirm.hide();
		scwin.fn_searchScheduleInfo();
	}else{
		dma_schdInfo.set("SCH_COLOR","#076aff");
		dma_schdInfo.set("SCH_WRITER",scwin.param.data.writer);
	}

};

/**
 * 일정 단건조회
 */
scwin.fn_searchScheduleInfo = function () {
	dma_schdInfo.set("SCH_IDX",scwin.param.data.schInfo.SCH_IDX);
	com.sbm.execute(sbm_schdInfo);
	//callback
	scwin.sbm_schdInfo_submitdone = function(e) {
		btn_colorPicker.setStyle("background-color",dma_schdInfo.get('SCH_COLOR'));
		edi_1.setHTML(dma_schdInfo.get('SCH_CONT'));
	};

};

/* 일정등록 */
scwin.btn_confirm_onclick = function(e) {
	com.win.confirm($p, '일정을 등록하시겠습니까?', function(e){
		if(e.clickValue){
			let _current = dma_schdInfo.getJSON().SCH_START.substr(0,6);
			dma_schdInfo.set("SCH_CONT", edi_1.getHTML());
			dma_schdInfo.set("SCH_TITLE",gcm.ext.moca.getTitleFromEditor(edi_1));
			dma_schdInfo.set("SCH_CURRENTMONTH",_current);
			com.sbm.execute(sbm_insertSchedule);
		}
	});

	scwin.sbm_insertSchedule_submitdone = function(e) {
		if(e.responseJSON.status == "S"){
			var param = {
				"message":"등록이 완료되었습니다."
			};
			com.win.closePopup(param);
		}
	};
};

/* 팝업취소 */
scwin.btn_cancel_onclick = function(e) {
	com.win.closePopup();
};

/* 일정 논리삭제 */
scwin.btn_del_onclick = function(e) {
    com.win.confirm($p, '해당 게시물을 삭제하시겠습니까?', function(e){
		if(e.clickValue){
			dma_schdInfo.set("STATUS", "U");
			dma_schdInfo.set("SCH_DELYN","Y");
			com.sbm.execute(sbm_updateSchedule);
		}
	});
};

/* 일정 물리삭제 */
scwin.btn_adminDel_onclick = function(e) {
	var data = {callbackFn: "scwin.delPop_callback" };
	var options = {
		id: "delPop",
		popupName: "일정삭제",
		modal: true,
		useMaximize : true,
		width: 420, height: 116
	};
	com.win.openPopup("/ui/delPop.xml", options, data);
};
scwin.delPop_callback = function(e){
	if(e.delYn){
		com.win.confirm($p, '삭제하시면 데이터를 더이상 복구하실 수 없습니다.\n 해당 게시물을 삭제하시겠습니까?', function(e){
			if(e.clickValue){
				dma_schdInfo.set("STATUS", "D");
				com.sbm.execute(sbm_updateSchedule);
			}
		})
	}
}

/* 일정 수정 */
scwin.btn_update_onclick = function(e) {
	com.win.confirm($p, '해당 게시물을 수정하시겠습니까?', function(e){
		if(e.clickValue){
			scwin.btn_update_exe();
		}
	});
};

scwin.btn_done_onclick = function(e) {
	com.win.confirm($p, '완료변경처리 하시겠습니까?', function(e){
		if(e.clickValue){
			let _cont = edi_1.getHTML();
			_cont = _cont.replace(/^(&nbsp;\s*)*/g, '');
			if(!_cont.startsWith('[완료]')){
				edi_1.setHTML('[완료]'+_cont);
			}else{
				edi_1.setHTML(_cont.replace(/^(\[완료\])/g,''));
			}
			scwin.btn_update_exe();
		}
	});
};

scwin.btn_update_exe = function(e) {
	dma_schdInfo.set("STATUS", "U");
	dma_schdInfo.set("SCH_CONT", edi_1.getHTML());
	let _title =  gcm.ext.moca.getTitleFromEditor(edi_1);
	_title = _title.split('---')[0]+'---'+dma_schdInfo.get('SCH_IDX');
	dma_schdInfo.set("SCH_TITLE",_title);
	com.sbm.execute(sbm_updateSchedule);
};


/* 일정 수정,삭제 callback */
scwin.sbm_updateSchedule_submitdone = function(e) {
	if(e.responseJSON.status == "S"){
		if(dma_schdInfo.get('STATUS') == "D" || dma_schdInfo.get('SCH_DELYN') == "Y"){
			var param = {
				"message":"삭제가 완료되었습니다."
			};
			com.win.closePopup(param);
		}else{
			var param = {
				"message":"수정이 완료되었습니다."
			};
			com.win.closePopup(param);
		}
	}
};

scwin.btn_color_onclick = function(e) {
	var data = {callbackFn: "scwin.colorPop_callback" };
	var options = {
		id: "colorPop",
		className: "fixfix",
		popupName: "색상변경",
		modal: true,
		useMaximize : true,
		width: 420, height: 227
	};
	com.win.openPopup("/ui/colorPop.xml", options, data);
};
scwin.colorPop_callback = function(e){
	if(e.color){
		dma_schdInfo.set("SCH_COLOR", e.color);
		btn_colorPicker.setStyle("background-color",e.color);
	}
};
