mapper.select:
/*selectBoardList 게시물 목록조회*/ SELECT A.BOARD_PIDX, A.BOARD_IDX, A.BOARD_DELYN, A.BOARD_TITLE, A.BOARD_WRITER, DATE_FORMAT(A.BOARD_DATE, '%Y-%m-%d %H:%i:%s') as BOARD_DATE , DATE_FORMAT(A.UPDATE_DATE, '%Y-%m-%d %H:%i:%s') as UPDATE_DATE , A.BOARD_TYPE, COUNT(F.CONTENT_ID) AS CNT, A.BOARD_REPLYCNT FROM ${BOARD_TABLE} A LEFT OUTER JOIN ${BOARD_FILE_TABLE} F ON A.BOARD_IDX = F.CONTENT_ID
WHERE 1=1 AND A.BOARD_DELYN = 'N' AND A.BOARD_TYPE = #{BOARD_TYPE}
AND A.BOARD_WRITER like CONCAT('%', #{BOARD_WRITER}, '%')
AND (
A.BOARD_CONT like CONCAT ('%', #{cont}, '%')
)
AND A.BOARD_DATE >= STR_TO_DATE(CONCAT(#{FROM},' 00:00:00'), '%Y%m%d %H:%i:%s') AND A.BOARD_DATE <= STR_TO_DATE(CONCAT(#{TO},' 23:59:59'), '%Y%m%d %H:%i:%s')
GROUP BY A.BOARD_IDX ORDER BY A.UPDATE_DATE DESC, A.BOARD_DATE DESC
limit #{OFFSET_ROW} , #{PAGE_SIZE}

	/*selectBoardFileList 게시판첨부파일조회*/
    SELECT *
    FROM ${BOARD_FILE_TABLE} A
    WHERE 1=1

        AND A.CONTENT_ID = #{BOARD_IDX}

    ORDER BY A.FILE_IDX ASC

mapper.insert:
   		 /*"insertT_FILE" 파일등록*/
           INSERT INTO `T_FILE` 
           (
           `FILE_IDX`, `CONTENT_ID`, `FILE_NAME`, `FILE_PATH_SUB`, `FILE_PATH_PARENT`, `FILE_SIZE`, 
           `FILE_ID`, `FILE_DESC`, `THUMBNAIL`, `UPLOAD_SYS`
           ) 
           VALUES
			(
			#{FILE_IDX}, #{CONTENT_ID}, #{FILE_NAME}, #{FILE_PATH_SUB}, #{FILE_PATH_PARENT}, #{FILE_SIZE}, 
			#{FILE_ID}, #{FILE_DESC}, #{THUMBNAIL}, #{UPLOAD_SYS}
			)

			select max(FILE_IDX)+1 from T_FILE

mapper.delete:
        /*delete T_FILE 물리삭제*/
      	DELETE FROM T_FILE
		WHERE 1=1
		AND CONTENT_ID = #{CONTENT_ID}

			AND FILE_IDX = #{FILE_IDX}

mapper.update:
/*updateT_FILE 파일설명수정*/
UPDATE T_FILE SET 
   FILE_DESC = #{FILE_DESC}
WHERE 1=1
AND FILE_IDX=#{FILE_IDX}
AND CONTENT_ID=#{CONTENT_ID}

/*updateBoardInfo 게시물수정*/ UPDATE ${BOARD_TABLE} SET BOARD_TYPE = #{BOARD_TYPE},
BOARD_DELYN = #{BOARD_DELYN},
BOARD_TITLE = #{BOARD_TITLE}, BOARD_CONT = #{BOARD_CONT}, BOARD_WRITER = #{BOARD_WRITER}, UPDATE_DATE = CURRENT_TIMESTAMP WHERE 1=1 AND BOARD_IDX=#{BOARD_IDX}

mapper.delete:
        /*deleteBoard 게시물 물리삭제*/
        		DELETE FROM ${BOARD_TABLE}
				WHERE 1=1
  				AND BOARD_IDX=#{BOARD_IDX}

mapper.insert:
/*insertSchedule 스케줄러 작성*/ INSERT INTO MT_SCHEDULE( SCH_IDX, SCH_TITLE, SCH_CONT, SCH_WRITER, SCH_CREATE_DTTM, SCH_CURRENTMONTH, SCH_START, SCH_END, SCH_COLOR, SCH_DELYN, SCH_SENDYN, SCH_SUCCESSYN )VALUES ( #{SCH_IDX}, CONCAT(#{SCH_TITLE},'---',CAST(#{SCH_IDX} AS CHAR)), #{SCH_CONT}, #{SCH_WRITER}, CURRENT_TIMESTAMP, #{SCH_CURRENTMONTH}, #{SCH_START}, #{SCH_END}, #{SCH_COLOR}, 'N', 'N', 'N'
)
/* selectKey in insertSchedule */ select max(SCH_IDX)+1 from MT_SCHEDULE

        /*insertT_SCHEDULE*/
    	INSERT INTO mocaerp.T_SCHEDULE
		(
			SCH_WRITER, SCH_START, SCH_IDX, 
			SCH_TITLE, SCH_CONT, SCH_CREATE_DTTM, SCH_CURRENTMONTH, 
			SCH_START_TIME, SCH_END, SCH_END_TIME, SCH_COLOR, 
			SCH_DELYN, SCH_SUCCESSYN, SCH_SENDYN
		)
		VALUES(
			#{SCH_WRITER}, #{SCH_START}, #{SCH_IDX}, 
			#{SCH_TITLE}, #{SCH_CONT}, CURRENT_TIMESTAMP, #{SCH_CURRENTMONTH}, 
			'', '', '', '',
			'N', 'N', 'N'
		);

        	/* selectKey in INS_SCHEDULE */
			SELECT NVL(MAX(SCH_IDX)+1,1) FROM T_SCHEDULE
			WHERE 1=1
			AND SCH_WRITER 	= #{SCH_WRITER}
			AND SCH_START 	= #{SCH_START}

mapper.select:
/*SEL_SCHEDULELIST*/ SELECT SCH_IDX, case when SCH_SUCCESSYN= 'Y' then CONCAT('✔',SCH_TITLE) else SCH_TITLE END AS SCH_TITLE2, SCH_TITLE, SCH_CONT, SCH_WRITER, SCH_CREATE_DTTM, SCH_CURRENTMONTH, SCH_START,
SCH_START_TIME, SCH_END, SCH_END_TIME,
SCH_COLOR , SCH_COLOR AS FUL_COLOR, SCH_DELYN, SCH_SENDYN, SCH_SUCCESSYN FROM T_SCHEDULE WHERE 1=1 AND SCH_DELYN = 'N' AND (SCH_CURRENTMONTH = #{SCH_CURRENTMONTH_PREV} OR SCH_CURRENTMONTH = #{SCH_CURRENTMONTH} OR SCH_CURRENTMONTH = #{SCH_CURRENTMONTH_NEXT} )

mapper.update:
/*updateScheduleInfo 스케줄러 수정*/ UPDATE MT_SCHEDULE SET SCH_CONT = #{SCH_CONT}, SCH_TITLE = #{SCH_TITLE}, SCH_START = #{SCH_START}, SCH_END = #{SCH_END}, SCH_WRITER = #{SCH_WRITER},
SCH_DELYN = #{SCH_DELYN},
SCH_SUCCESSYN = #{SCH_SUCCESSYN}, SCH_COLOR = #{SCH_COLOR} WHERE 1=1 AND SCH_IDX=#{SCH_IDX}

mapper.delete:
        /*deleteSchedule 스케줄러 물리삭제*/
        		DELETE FROM MT_SCHEDULE
				WHERE 1=1
  				AND SCH_IDX=#{SCH_IDX}

mapper.update:
        /* updateScheduleSendSmsYn 스케줄러 일정 SMS보내기 YN업데이트*/
        UPDATE MT_SCHEDULE SET 
        	SCH_SENDYN = 'Y'
        WHERE 1=1
		AND SCH_IDX=#{SCH_IDX}
