mapper.select:
/*selectBoardList 게시물 목록조회*/ SELECT A.BOARD_PIDX, A.BOARD_IDX, A.BOARD_DELYN, A.BOARD_TITLE, A.BOARD_WRITER, DATE_FORMAT(A.BOARD_DATE, '%Y-%m-%d %H:%i:%s') as BOARD_DATE , DATE_FORMAT(A.UPDATE_DATE, '%Y-%m-%d %H:%i:%s') as UPDATE_DATE , A.BOARD_TYPE, COUNT(F.BOARD_IDX) AS CNT, A.BOARD_REPLYCNT FROM ${BOARD_TABLE} A LEFT OUTER JOIN ${BOARD_FILE_TABLE} F ON A.BOARD_IDX = F.BOARD_IDX
WHERE 1=1 AND A.BOARD_DELYN = 'N' AND A.BOARD_PIDX = A.BOARD_IDX AND A.BOARD_TYPE = #{BOARD_TYPE}
AND A.BOARD_WRITER like CONCAT('%', #{BOARD_WRITER}, '%')
AND (
A.BOARD_CONT like CONCAT ('%', #{contents}, '%')
)
AND A.BOARD_DATE >= STR_TO_DATE(CONCAT(#{FROM},' 00:00:00'), '%Y%m%d %H:%i:%s') AND A.BOARD_DATE <= STR_TO_DATE(CONCAT(#{TO},' 23:59:59'), '%Y%m%d %H:%i:%s')
GROUP BY A.BOARD_IDX ORDER BY A.UPDATE_DATE DESC, A.BOARD_DATE DESC

	/*selectBoardFileList 게시판첨부파일조회*/
    SELECT *
    FROM ${BOARD_FILE_TABLE} A
    WHERE 1=1

        AND A.BOARD_IDX = #{BOARD_IDX}

    ORDER BY A.BOARDFILE_IDX DESC

mapper.update:
/*updateBoardInfo 게시물수정*/ UPDATE ${BOARD_TABLE} SET BOARD_TYPE = #{BOARD_TYPE},
BOARD_DELYN = #{BOARD_DELYN},
BOARD_PIDX = #{BOARD_PIDX}, BOARD_TITLE = #{BOARD_TITLE}, BOARD_CONT = #{BOARD_CONT}, BOARD_WRITER = #{BOARD_WRITER}, UPDATE_DATE = CURRENT_TIMESTAMP WHERE 1=1 AND BOARD_IDX=#{BOARD_IDX}
